import React, { useState } from 'react';
import { FaSearch } from 'react-icons/fa';
import { Link } from 'react-router-dom';
import {    BsPlusLg } from 'react-icons/bs';
import NoteItem from '../components/NoteItem';

const Notes = ({notes}) => {

  const [searchShow,setSearchShow] = useState(false);
  const showSearch = () =>{
    setSearchShow(!searchShow);
  }

  const [searchText,setSearchText] = useState('');
  const [filteredNotes,setFilteredNotes] = useState(notes);

  const handleSearch = (e) =>{
    setSearchText(e.target.value);

  }



  return (
    <section>
        <header className="notes__header">
            {
              !searchShow && <h2>My Notes</h2>}
            {
             searchShow && <input type="text" value={searchText} onChange={handleSearch} autoFocus placeholder='Keyword...'/> 
            }
             
            <button className='btn' onClick={showSearch}><FaSearch /></button>
        </header>
        <div className="notes__container">
            {
                filteredNotes.map((note) => <NoteItem key={note.id} note={note}/>)
            }
        </div>

        <Link className='btn add__btn' to="/create-note"><BsPlusLg /></Link>
    </section>
  )
}

export default Notes
